<template>
  <div class="login-page"
       id="login-placeholder">
    <div class="login-content">
      <div class="header-logo">
        <div class="logo">
          <img src="../assets/images/login-logo.png"
               alt="Top Education Institute" />
        </div>
      </div>
      <div class="login-box">
        <div class="login-logo">
          <img src="../assets/images/login-logo.png"
               alt="Top Education Institute" />
        </div>

        <div class="login-head">
          <h1>Student Login</h1>
        </div>
        <div class="login-form">
          <el-form :model="formData"
                   :rules="rules"
                   ref="ruleForm">
            <el-form-item prop="username">
              <el-input v-model="formData.username"
                        placeholder="Student ID"></el-input>
            </el-form-item>
            <el-form-item prop="password">
              <el-input v-model="formData.password"
                        type="password"
                        placeholder="Password"></el-input>
            </el-form-item>
            <div class="login-bar">
              <button @click.prevent="formSubmit"
                      class="btn-login">Login</button>
            </div>
            <div class="forgot-link">
              <router-link to="forgot"
                           class="forgot">Forgot Password?</router-link>
            </div>
          </el-form>
        </div>

      </div>
      <div class="login-pic"></div>
    </div>

    <div class="login-footer">
      &copy; 2019 Roweyda Mdt Info Tech Ltd. All rights reserved.
    </div>

    <div class="support-logo"><img
           src="data:image/svg+xml;base64,"
           alt=""></div>

    <div class="login-bg">
      <div class="bg-box-1"></div>
      <div class="bg-box-2"></div>
      <div class="bg-box-3"></div>
    </div>
  </div>
</template>
<script>
import { reactive, ref } from 'vue'
// import { router, route } from '@/utils/helper.js'
import { useRoute, useRouter } from 'vue-router'
import request from '@/utils/request.js'
import store from '@/stores'
export default {
  setup() {
    const formData = reactive({
      username: '',
      password: '',
      loginTarget: 'Current_Student_Entrance'
    })
    const router = useRouter()
    const ruleForm = ref(null)
    const rules = {
      username: [{ required: true, message: 'Please enter your Student ID.', trigger: 'blur' }, { min: 6, max: 20, message: 'Student ID length invalid.' }],
      password: [{ required: true, message: 'Please enter your password correct.', trigger: 'blur' }, { min: 6, max: 20, message: 'Password length invalid.' }]
    }
    const formSubmit = () => {
      const url = `/public/auth/user/login/username`
      ruleForm.value.validate(valid => {
        if (valid) {
          request.post(url, { ...formData }).then(res => {
            store.dispatch('commitUserInfo', res.data)
            store.dispatch('commitToken', res.data.token)
            router.push({
              path: '/home'
            })
          })
        }
      })

    }
    return {
      ruleForm,
      formData,
      rules,
      formSubmit
    }
  }
}
</script>
<style>
#login-placeholder .placeholder {
  top: 0;
}
</style>
